<div class="row">	<div class="col-lg-12">		<div class="card">			<div class="card-header">				<h3 class="card-title float-left"><?php echo $text_form; ?></h3>				<div class="panel-tools float-right">					<button type="submit" data-toggle="tooltip" title="<?php echo $button_save; ?>" class="btn btn-danger" form="form-formula"><i class="fa fa-save"></i></button>					<a href="<?php echo $cancel; ?>" data-toggle="tooltip" title="<?php echo $button_cancel; ?>" class="btn btn-primary"><i class="fa fa-reply"></i></a>				</div>			</div>			<div class="card-body">				<?php echo form_open_multipart(null,array('class' => 'form-horizontal', 'id' => 'form-formula','role'=>'form')); ?>					<div class="row">						<?php foreach($fields as $field_name=>$field){?>						<div class="col-6">							<div class="card card-border">								<div class="card-header border-info bg-transparent pb-0">									<h3 class="card-title text-info"><?=$field_name?></h3>								</div>								<div class="card-body">									<table class="table table-striped">										<tbody>										<?php foreach($field as $f){?>										<tr data-code="<?=$f->field_code?>">										  <td><?=$f->field_name?></td>										  <td><?=$f->field_code?></td>										</tr>										<?}?>																			  </tbody>									</table>								</div>							</div>						</div>						<?} ?>					</div>										<div class="row">						<div class="col-md-6">							<div class="form-group row required">								<label class="col-md-3 control-label" for="input-firstname">Branch</label>								<div class="col-md-9">									<?php echo form_dropdown('branch_id', option_array_value($branches, 'id', 'name',array('0'=>'All Branch')), set_value('branch_id', $branch_id),"id='branch_id' class='form-control select2'"); ?>												<?php echo form_error('branch_id', '<div class="text-danger">', '</div>'); ?>								</div>							</div>							<div class="form-group row required">								<label class="col-md-3 control-label" for="input-firstname">Formula</label>								<div class="col-md-9">									<?php echo form_input(array('class'=>'form-control','name' => 'formula', 'id' => 'formula', 'placeholder'=>"formula",'value' => set_value('formula', $formula,false))); ?>									<?php echo form_error('formula', '<div class="text-danger">', '</div>'); ?>																</div>																</div>						</div>						<div class="col-md-6">							<div class="form-group row required">								<label class="col-md-3 control-label" for="input-email">Formula Code</label>								<div class="col-md-9">									<?php echo form_input(array('class'=>'form-control','name' => 'code', 'id' => 'code', 'placeholder'=>"Formula Code",'value' => set_value('code', $code))); ?>									<?php echo form_error('code', '<div class="text-danger">', '</div>'); ?>																</div>							</div>							<div class="form-group button-list">								<button type="button" data-code="(" class="btn btn-primary waves-effect waves-light"><strong>(</strong></button>								<button type="button" data-code=")" class="btn btn-primary waves-effect waves-light"><strong>)</strong></button>								<button type="button" data-code="+" class="btn btn-success waves-effect waves-light"><i class="fa fa-plus"></i></button>								<button type="button" data-code="-" class="btn btn-success waves-effect waves-light"><i class="fa fa-minus"></i></button>								<button type="button" data-code="*" class="btn btn-success waves-effect waves-light"><i class="fa fa-asterisk "></i></button>								<button type="button" data-code="/" class="btn btn-success waves-effect waves-light"><strong>/</strong></button>								<button type="button" class="btn btn-warning waves-effect waves-light" id="clear">Clear</button>								<button type="button" class="btn btn-danger waves-effect waves-light" id="clearall">Clear All</button>							</div>						</div>					</div>				<?php echo form_close(); ?>			</div> <!-- panel-body -->		</div> <!-- panel -->	 </div> <!-- col --></div><?php js_start(); ?><script type="text/javascript"><!--    $(document).ready(function() {		$("[data-code]").click(function(){			var code=$(this).data('code');			$("#formula").val(function() {				return this.value + code;			});		});				$("#clear").click(function(){			$("#formula").val(				function(index, value){					return value.substr(0, value.length - 1);			})		});				$("#clearall").click(function(){			$("#formula").val('');		})	});	//--></script><?php js_end(); ?>