ss("display","none");							},			dataType:'json'		},	});		var bar = $('.progress-bar');    var percent = $('.percent');    		$('.upload-form').ajaxForm({		dataType:'json',        beforeSend: function() {            var percentVal = '0%';            bar.width(percentVal);            percent.html(percentVal);        },        uploadProgress: function(event, position, total, percentComplete) {            var percentVal = percentComplete + '%';            bar.width(percentVal);            percent.html(percentVal);        },        complete: function(xhr) {            //console.log(xhr);			//location=xhr.redirect;        },		success: function(data){			console.log(data);			if(data.success){				location=data.redirect;			}					}    });		$('#bemployee').on('change',function () {		$('.upload-form').submit();	})		});function thisFileUpload() {	document.getElementById("bemployee").click();};//--></script><?php js_end(); ?>