<div class="row">	<div class="col-lg-12">		<div class="card">			<div class="card-header">				<h3 class="card-title float-left"><?php echo $heading_title; ?></h3>				<div class="panel-tools float-right">					<a href="<?php echo $add; ?>" data-toggle="tooltip" title="<?php echo $button_add; ?>" class="btn btn-primary"><i class="fa fa-plus"></i></a>					<button type="button" data-toggle="tooltip" title="<?php echo $button_delete; ?>" class="btn btn-danger" onclick="confirm('<?php echo $text_confirm; ?>') ? $('#form-salary').submit() : false;"><i class="fa fa-trash-o"></i></button>				</div>			</div>			<div class="card-body">				<form id="form-filter" class="form-horizontal">					<div class="form-layout">						<div class="row">							<div class="col-lg-3">								<div class="form-group mg-b-10-force">									<label class="form-control-label">Branch: <span class="tx-danger">*</span></label>									<?php echo form_dropdown('branch_id', option_array_value($branches, 'id', 'name'), set_value('fbranch', ''),"id='branch_id' class='form-control select2'"); ?>									</div>							</div>														<div class="col-lg-3">								<div class="form-group mg-b-10-force">									<label class="form-control-label">Department: <span class="tx-danger">*</span></label>									<?php echo form_dropdown('department_id', option_array_value($departments, 'id', 'name',array(''=>'Select Department')), set_value('department_id', ''),"id='department_id' class='form-control select2'"); ?>									</div>							</div><!-- col-4 -->							<div class="col-lg-3">								<div class="form-group mg-b-10-force">									<label class="form-control-label">Designation: <span class="tx-danger">*</span></label>									<?php echo form_dropdown('designation_id', option_array_value($designations, 'id', 'name',array(''=>'Select Designation')), set_value('designation_id', ''),"id='designation_id' class='form-control select2'"); ?>								</div>							</div><!-- col-4 -->														<div class="col-lg-3">								<label class="form-control-label">&nbsp;</label>								<div class="form-layout-footer">									<button type="button" id="btn-filter" class="btn btn-primary">Filter</button>									<button type="button" id="btn-reset" class="btn btn-default">Reset</button>								</div>							</div>						</div>																			<!-- form-layout-footer -->					</div><!-- form-layout -->				</form>				<hr>				<form action="<?php echo $delete; ?>" method="post" enctype="multipart/form-data" id="form-salary">          					<div class="row">						<div class="col-md-12 col-sm-12 col-12">							<table id="salary_list" class="table table-striped table-bordered dt-responsive nowrap" style="border-collapse: collapse; border-spacing: 0; width: 100%;">								<thead>									<tr>										<th style="width: 1px;" class="text-center no-sort"><input type="checkbox" onclick="$('input[name*=\'selected\']').prop('checked', this.checked);" /></th>										<th>Employee Name</th>										<th>Card No</th>										<th>Basic</th>										<th>Allowance</th>										<th>Deduction</th>										<th>Total</th>										<th class="text-right no-sort">Actions</th>									</tr>								</thead>							</table>						</div>					</div>				</form>			</div>		</div>	</div></div><?php js_start(); ?><script type="text/javascript"><!--$(function(){	table=$('#salary_list').DataTable({		"processing": true,		"serverSide": true,		"columnDefs": [			{ targets: 'no-sort', orderable: false }		],		"ajax":{			url :"<?=$datatable_url?>", // json datasource			type: "post",  // method  , by default get			data: function ( data ) {				data.branch_id = $('#branch_id').val();				data.department_id = $('#department_id').val();				data.designation_id = $('#designation_id').val();			},			beforeSend: function(){				$('.alert-dismissible, .text-danger').remove();				$("#salary_list_wrapper").LoadingOverlay("show");			},			complete: function(){				$("#salary_list_wrapper").LoadingOverlay("hide");			},			error: function(){  // error handling				$(".salary_list_error").html("");				$("#salary_list").append('<tbody class="salary_list_error"><tr><th colspan="3">No data found.</th></tr></tbody>');				$("#salary_list_processing").css("display","none");							},			dataType:'json'		},	});		$('#btn-filter').click(function(){ //button filter event click		table.ajax.reload();  //just reload table	});	$('#btn-reset').click(function(){ //button reset event click		$('#form-filter')[0].reset();		table.ajax.reload();  //just reload table	});});//--></script><?php js_end(); ?>